void greenTint( out vec4 outColor, in vec4 color){
    outColor = vec4(0.5*color.r, color.g, 0.5*color.b, color.a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 pixColor = texture(iChannel0,uv);
    vec4 solidYellow = vec4(0.0,1.0,0.0,1.0);

    if(pixColor.g > 0.5 && pixColor.r < 0.3 && pixColor.b<0.3){
        pixColor = texture(iChannel1,uv);
        greenTint(pixColor, pixColor);
    }

    fragColor = pixColor;//vec4(uv,0.5+0.5*sin(iTime),1.0);
}
