//change to 0 to disable dynamic noise
#define UseTime 1

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage(out vec4 col, in vec2 uv) {
	uv /= iResolution.xy;

    vec3 green = vec3(0.0, 1.0, 0.0);

    #if UseTime == 1
    float random = rand(uv * vec2(0.5 + 0.5 * sin(iTimeDelta)));
    #else
    float random = rand(uv);
    #endif
    random = mix(random, 1.0, 1.0 - clamp(uv.y + 0.15, 0.0, 1.0));

    float weight = uv.y + 0.4 * random;
    weight = 1.0 - clamp(weight, 0.0, 1.0);

    col.rgb = mix(vec3(0.0), green, weight);

    col = vec4(col.rgb, 1.0);
}
