void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    highp int rando = int(iTime * 10.0);
    rando *= 0x9e3779b1;

    highp int r = int(fragCoord.x + float(rando));

    rando *= 0x9e3779b1;

    highp int g = int(fragCoord.y + float(rando));

    rando *= 0x9e3779b1;

    highp int b = int(fragCoord.x + fragCoord.y + float(rando));

    r *= 0x9e3779b1;
    g *= 0x9e3779b1; 
    b *= 0x9e3779b1; 

    vec3 color = vec3(float(r), float(g), float(b));
    color /= 2000000000.0;

	fragColor = vec4(color, 1.0);
}
