vec2 offset [9];
float kernel [9];

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy;
    vec2 step = 1.0 / res;
    vec2 uv = fragCoord / res;
    vec2 coord = -1.0 + 2.0 * uv;
    coord *= 0.995;
    coord = coord * 0.5 + 0.5;

    float r = rand(uv + iTime*0.01);
    r = -1.0 + 2.0*r;

    offset[0] = vec2(-step.x, -step.y);
    offset[1] = vec2(0.0, -step.y);
    offset[2] = vec2(step.x, -step.y);

    offset[3] = vec2(-step.x, 0.0);
    offset[4] = vec2(0.0, 0.0);
    offset[5] = vec2(step.x, 0.0);

    offset[6] = vec2(-step.x, step.y);
    offset[7] = vec2(0.0, step.y);
    offset[8] = vec2(step.x, step.y);

    kernel[0] = 0.0; kernel[1] = -0.25; kernel[2] = 0.0;
    kernel[3] = -0.25; kernel[4] = 1.0; kernel[5] = -0.25;
    kernel[6] = 0.0; kernel[7] = -0.25; kernel[8] = 0.0;

    vec4 sum = texture(iChannel0, coord);

    for (int i = 0; i < 9; i++) {
        vec4 color = texture(iChannel0, coord + offset[i]);
        sum += color * kernel[i];
    }
	sum = mix(sum,texture(iChannel0, uv), vec4(sin(iTime*.7)));
    if(iFrame < 10 || iMouse.z > 0.0 || sin(iTime*0.5) > 0.0 ){
    	fragColor = texture(iChannel1, uv);
    } else {
    	fragColor = clamp(sum+ r*0.025, vec4(0.0), vec4(1.0));//texture(iChannel0, uv);
        fragColor.a = 1.0;
    }
}

vec2 offset [9];
float kernel [9];


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 res = iResolution.xy;
    vec2 step = 0.5 / res;
    vec2 uv = fragCoord / res;
    vec2 coord = uv;


    offset[0] = vec2(-step.x, -step.y);
    offset[1] = vec2(0.0, -step.y);
    offset[2] = vec2(step.x, -step.y);

    offset[3] = vec2(-step.x, 0.0);
    offset[4] = vec2(0.0, 0.0);
    offset[5] = vec2(step.x, 0.0);

    offset[6] = vec2(-step.x, step.y);
    offset[7] = vec2(0.0, step.y);
    offset[8] = vec2(step.x, step.y);


    kernel[0] = 1.0; kernel[1] = 1.0; kernel[2] = 1.0;
    kernel[3] = 1.0; kernel[4] = 1.0; kernel[5] = 1.0;
    kernel[6] = 1.0; kernel[7] = 1.0; kernel[8] = 1.0;

    vec4 sum = vec4(0.0);

    for (int i = 0; i < 9; i++) {
        vec4 color = texture(iChannel0, coord + offset[i]);
        sum += color * kernel[i];
    }
	sum /= 9.0;

    fragColor = clamp(sum , vec4(0.0), vec4(1.0));
    fragColor.a = 1.0;
}
