void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //variable that decides the number of columns and rows
    float numColumnsAndRows = 10.0;

    //assigns the primary and secondary colors
    vec4 firstColor = vec4(1.0, 1.0, 1.0, 1.0);
    vec4 secondColor = vec4(0.0, 0.0, 0.0, 1.0);

	vec2 uv = fragCoord.xy / iResolution.xy;

    float xDir = uv.x;
    float yDir = uv.y;

	//use floor to get which column or row the pixel is in, and then use mod to check if it is even or odd
    float fmodResult = mod(floor(numColumnsAndRows * xDir) + floor(numColumnsAndRows * yDir), 2.0);

    //if odd, primary color. if even, secondary color
    if (fmodResult < 1.0)
    {
      fragColor = firstColor;
    } 
    else 
    {
      fragColor = secondColor;
    }
}
