void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 color = vec3(0.0);
    float alpha = 1.0;

    vec2 bl = step(vec2(0.1),uv);
    vec2 tr = step(vec2(0.1),1.0 - uv);
    color = vec3(bl.x * bl.y * tr.x * tr.y);

	fragColor = vec4(color,alpha);
}
