//Not at movie level quality, but at least it's at Meme level quality

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //Default colour
    vec3 defaultCol = vec3(1,1,1);

    //use texture colour
    //LusterCream
    vec3 col = texture(iChannel0, uv).rgb;

    //dick
    vec3 col2 = texture(iChannel1, uv).rgb;

    //britney
    //vec3 col3 = texture(iChannel2, uv).rgb;

    //if(col.g > 0.4 && col.r < 0.4 && col.b < 0.4)
    	//{col = col3;}

    //if enough green and not enough others, use default
    //Unfortunalely these numbers we decided on by trial an error
	//Would like to be getting the green of the green screen from a pixel we know is the correct colour
    if(col.g > 0.4 && col.r < 0.4 && col.b < 0.4)
    	{col = col2;}

    // Output to screen
    fragColor = vec4(col,1.0);
}
