void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;

    //minus .5 from x and y
    uv.xy -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    // the diamater of my circle
    float d = length(uv);
    float r = 0.3;

    float c = smoothstep(r, r-0.5, d);

    //Makes black and white easy
    //float c = d;

    //if(d < .3) c = 1.; else c = 0.;


    // Output to screen
    fragColor = vec4(0.0,c/(iMouse.xy/iResolution.xy+0.01),1.0);
}
