//I'm just learning how shaders work - Nick S.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy/iResolution.xy; // 0 <> 1

    uv -=.5; // -0.5 <> 0.5

    uv.x *= iResolution.x/iResolution.y; // Fixes aspect ratio

    float d = length(uv);
    float n = fract (iTime+sin(uv.x * 100. + uv.y * 1.)*1.55 );
    float r = 0.04* sin(iTime*10.)+0.2;
    r += 0.05 * sin(iTime);

    //float r = 0.5;

    float c = smoothstep(r, r-n, d);

    //c *= vec3(.6, .4, .2);

    fragColor = vec4(vec3(c * vec3(.2, 0.8, 1.1)), 1.0);
}
