float circle(vec2 p,float r){
  return length(p) - r;
}

float rect(vec2 p, vec2 d){
  vec2 m = abs(p) - d; 
  return min(max(m.x,m.y),0.0) + length(max(m,0.0));
}

float roundRect(vec2 p, vec2 d, float r){
  vec2 m = abs(p) - d ;
  return min(max(m.x,m.y) ,0.0) + length(max(m,0.0)) - r;
}

float fill(float d){
  return step(d,0.0);   
}

float stroke(float d,float w){
  return abs(step(d,w*0.5) - step(d,-w *0.8));   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec2 uv = fragCoord.xy / iResolution.xy;
  uv = uv * 2.0 - 1.0;
  uv.x *= iResolution.x / iResolution.y;
  uv += vec2(0.0,-0.15);
  float d = roundRect(uv - vec2(0.0,-0.15),vec2(0.15,1.2)*0.1,0.4);
  d = stroke(d,0.05) * step(uv.y,-0.1);
  d += fill(rect(uv - vec2(0.0,-0.72),vec2(0.10,0.035)));
  d += stroke(circle(uv - vec2(0.44,-0.3) ,0.11),0.02) * step(1.0 - uv.x,0.6);
  d += fill(circle(uv -vec2(0.11,-0.72),0.035));
  d += fill(circle(uv -vec2(-0.11,-0.72),0.035));
  vec2 sc = uv + vec2(0.00,0.25);
  d += stroke(circle(sc,0.1),0.035) * step(-0.75*sc.x + sc.y,0.0);
  vec2 bc = uv + vec2(-0.1,-0.25);
  d += stroke(circle(bc,0.2),0.035) * step(bc.x,0.0) * step(0.5*bc.x  - bc.y,0.0);
  d += stroke(uv.x + 0.425*uv.y,0.035) * step(0.75*uv.x - uv.y - 0.3,0.0) * (1.0-step(0.75*uv.x - uv.y + 0.28,0.0));
  fragColor = vec4(d);

}
