vec4 ledMatrix( vec2 uv ){
    float gridResolution = iResolution.x/3.;

    vec2 uvDots = uv;
    uvDots *= gridResolution;
	uvDots = fract(uvDots)- 0.5;    

    float spacing = 1.8;
    float cellWidth = 5.5;

    float CircleRed =   1. - length( uvDots * vec2(cellWidth,.1) - vec2(-spacing,0.));
    float CircleGreen = 1. - length( uvDots * vec2(cellWidth,.1) - vec2(0.,0.));
    float CircleBlue =  1. - length( uvDots * vec2(cellWidth,.1) - vec2(spacing,0.));

    float Circle = (1. - length( uvDots )*.27);
    Circle *= 1.8;

    vec4 tex = texture(iChannel0, floor(uv*gridResolution)/gridResolution );

    vec4 result;
    result.x = tex.x * CircleRed ;
    result.y = tex.y * CircleGreen *1.2;
    result.z = tex.z * CircleBlue ;

    return result*Circle;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 c = ledMatrix(uv);
    vec4 tex = texture(iChannel0, uv);

    fragColor = c;
}
