mat2 rot(float a){
    return mat2(-cos(a*iTime), sin(a),
                sin(a), cos(a));
}

void pMod1(inout float p, float size) {
	float halfsize = size*.5;
	p = mod(p + halfsize, size) - halfsize;
}

void pMod3(inout vec3 p, vec3 size) {
	p = mod(p + size*0.5, size) - size*0.5;
}

float sphere(vec3 p, float radius){
    return length(p)-radius;

}


float map(vec3 p)
{
    vec3 q = p;

    pMod3(q, vec3(.75, .75, .2));
    pMod3(q, vec3(0.75, 1., 0.6));

    pMod1(p.x, 1.);
    	//q.y = abs(q.y)-0.1;

    float s1 = sphere(p, 0.4*sin(iTime)); 
    float s2 = sphere(q, 0.5);
    float s3 = sphere(q, 0.5*cos(iTime));

    float disp = min(0.5 * (abs(sin(p.x*2.)) *
                       abs(sin(p.y*iTime/2.)) *
                       (sin(p.z*15.)) ), 50.);
    	s1 += disp;
    	s2 -= disp;
    	s3 *= disp;
    	//s1 -= disp;



  	float df1 = min(s1, s2); // Union
    float df2 = max(s2, s1); // Intersection
    float df3 = max(s1, s3); // Difference

    return df3;
}


float trace(vec3 origin, vec3 r) 
{
  float t = 0.0;
    for (int i = 0; i < 64; ++i) {
        vec3 p = origin + r * t;
        float d = map(p);
        t += d*0.35;
        }
    return t;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 color = vec3(sin(iTime), cos(iTime), 0.7);
    uv = uv *2.-1.; // Remap the space to -1. to 1.
    uv.x *= iResolution.x/iResolution.y;


   	float FOV = cos(sin(iTime));
   	vec3 ray = normalize(vec3(uv, FOV));
    ray.xy *= (cos(iTime))*.5;
    ray.yz *= (sin(iTime))*.5;
    ray.xz += sin(iTime);


    vec3 origin = vec3(iTime*.5, 0., -1.35);
    float t = trace(origin, ray);

    float expFog = .3 / (t* t* 0.3);

    vec3 fc = vec3(expFog);


    //fragColor = vec4((fc+color),1.0);
    fragColor = vec4(tan(fc+color),tan(iTime));
}
