float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    if( iResolution.x/2.0 == fragCoord.x ){

    	fragColor = vec4(1.0,0,0.0,1.0);

    }


    if( iResolution.y/2.0 == fragCoord.y ){

    	fragColor = vec4(0.0,0,1.0,1.0);

    }


    if (fragCoord.y == fragCoord.x){

    	fragColor = vec4(1.0,0,1.0,1.0);
    }

    float x = distance( iResolution.x/2.0 , fragCoord.x);
    float y = distance( iResolution.y/2.0 , fragCoord.y);
    y = rand( vec2( fragCoord.x+iDate.w ,fragCoord.y+iDate.w) );
    vec2 d = vec2(pow(x,sin(iDate.w)),y);
    fragColor.xy =  normalize( d );
    fragColor.z =  rand( vec2( fragCoord.x+iDate.w ,fragCoord.y+iDate.w) );



}
