#define speed 2.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = (vec2(iResolution.x-fragCoord.x, fragCoord.y) / iResolution.xy);
   float pixelId = mod(floor(fragCoord.x),2.) + 2.0 * mod(floor(fragCoord.y),2.);
   float timeId = mod(floor(iDate.w*speed),4.0);
   if(pixelId != timeId) discard;
    if(timeId == 3.) {
	   fragColor = texture(iChannel0, uv ) * 1.4;
    } else {    
	   fragColor = texture(iChannel0, uv ) * 1.4 * vec4(timeId==0.?0.:1.,timeId==1.?0.:1.,timeId==2.?0.:1.,1.);
    }
}
