void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float cx = iResolution.x * 0.5;
    float cy = iResolution.y * 0.5;
    float effect = (sin(iTime*4.0));
    float radius = effect * 300.0;

    float dx = cx - fragCoord.x;
    float dy = cy - fragCoord.y;
    float dist = sqrt(dx * dx + dy * dy);
    vec2 uv = fragCoord.xy / iResolution.xy;
    if (dist > radius)
    {
        fragColor = texture(iChannel0, uv);
    } else {
        float scale = (1.0 - (dist / radius)) * 0.0005 * (1.0 - effect);
		fragColor = texture(iChannel0, uv + vec2(dx * scale, dy * scale));
    }
}
