void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio;

    float modulo = 0.4;
    float valueA = floor(mod(uv.x,modulo)/(modulo*0.5));
    float valueB = floor(mod(uv.y,modulo)/(modulo*0.5));
    float value = abs(valueA-valueB);

	fragColor = vec4(value-uv.x*sin(iTime), value, value-uv.y*sin(iTime),1.0);
}

