void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.y = (-1.0 * uv.y) + 1.0;

    float pulsationScale = 0.5; // percent of the original size
    float minScale = 1.0 / pulsationScale;
    float maxScale = 1.0;
    float speed = 3.0;
    float scale = maxScale + (((sin(iTime * speed) * 0.5) + 0.5) * (minScale - maxScale));
    float center = 0.5;

    vec4 texture = texture(iChannel0, (((uv - center) * scale) + center));

	fragColor = texture;
}
