void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    float screenSizeY = iResolution.y;
	vec2 uv = fragCoord.xy / screenSizeY;
    uv -= 0.5 * vec2(1.0);
    uv *= 1.0 + 0.2 * sin(t * 0.31);
    uv += vec2(sin(t), t);
    uv = mod(uv + 0.25, 0.5) - 0.25;
    float r = uv.x * uv.x + uv.y * uv.y;
    float gray = mod(100.0 * pow(r, 2.0), 1.0) > 0.5 * sin(t) + 0.5 ? 1.0 : 0.0;
    fragColor = vec4(gray, gray, gray, 1.0);
}
