struct Hole{
	vec2 Center;
    float Rad;
};

float bounce( in float x, in float y )
{
    if(mod(x, y * 2.0) < y){
        return mod(x, y * 2.0);
    }else{
        return mod(-x, y * 2.0);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    Hole MyHole;
    //MyHole.Center = vec2(
    //    iResolution.x / 2.0 + sin(iTime) * iResolution.x / 3.0,
    //    iResolution.y / 2.0 + cos(iTime) * iResolution.y / 3.0
    //);
    MyHole.Center = vec2(
        bounce(iTime * 200.0, iResolution.x * 1.0),
        bounce(iTime * 200.0, iResolution.y * 1.0)
    );

    MyHole.Rad = 100.0 + sin(iTime) * 10.0;

	vec2 uv = fragCoord.xy / iResolution.xy;
 	uv.y = -uv.y;

    vec4 Vector = vec4(MyHole.Center,0.0, 1.0) - vec4(fragCoord,0,0);

    float Distance = length(Vector);


    if(Distance < MyHole.Rad)
    {
        float DistToRad = MyHole.Rad - Distance;

        Vector = normalize(Vector);
        Vector = Vector;

        vec2 CoordToUse = fragCoord.xy + Vector.xy * DistToRad;


        uv = CoordToUse/iResolution.xy;
        uv.y = -uv.y;

    }


    fragColor = texture(iChannel0, uv);
}
