//code released into the public domain via CC0
float spiral1(vec2 m) {
	float r = length(m);
	float a = atan(m.y, m.x);
	float v = sin(1000000.*abs(sin(iTime/10.))*(sqrt(r)-0.02*a-.3));
	return clamp(v,0.,1.);

}

vec3 spiral2(vec2 m) {
	float r = length(m);
	float a = atan(m.y, m.x);
    vec3 v;
	v.r = abs(sin(200.*(sqrt(r * .01)-0.02*a - .3 * iTime)));
	v.g = abs(sin(100.*(sqrt(r * .0105)-0.02*a - .31 * iTime)));
	v.b = abs(sin(50.*(sqrt(r * .011)-0.02*a - .32 * iTime)));
	return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    float screenSizeY = iResolution.y;
	vec2 uv = fragCoord.xy / screenSizeY;
    uv -= 0.5 * vec2(1.0);
    uv *= 5.0 + 0.2 * sin(t);
    uv += vec2(sin(t), t);
    uv = mod(uv + 0.25, 0.5) - 0.25;
    float r = uv.x * uv.x + uv.y * uv.y;
    float gray = mod(100.0 * pow(r, 3.0), 1.0) > 0.5 * sin(t*t) + 0.5 ? 1.0 : 0.0;
    gray = clamp(gray, 0.3, 1.);

    //spiral
    vec2 idk = fragCoord.xy / iResolution.y;
	vec2 m = iMouse.xy/ iResolution.y * 0.01;
	m = vec2(.9,.5) ;
	float v1 = spiral1(m-idk);
    vec3 v2 = spiral2(m-idk);

    fragColor = vec4((gray * v1 * v2.r)/2.0 + v2.r * abs(sin(t/ 4.123123)), 
                     (gray * v1 * v2.g)/2.0 + v2.g * abs(sin(t / 12.234235)),
                     (gray * v1 * v2.b)/2.0 + v2.b * abs(sin(t / 20.0989834)),
                     1.0);
}
