void mainImage( out vec4 o, in vec2 i ) {
    float t=iTime;
    vec2 d = 1. + .5 * vec2( sin(t), cos(t) );
    i = i / .3 / iResolution.xy - .7;
    if ( t>.1 ) {
    	t = smoothstep(.5,1.,length( i-d ));
    	if ( t > .65*d.x ) discard;
    }
	o = vec4(cos(d),sin(d)) / length( i );
}
