float t=iTime;

vec3 psychTexture(vec2 uv) {
    return vec3(sin(uv.x*2.0+uv.y*8.0*3.0)+0.2, sin(uv.x*2.0+0.666+uv.y*4.0*3.0-t*7.0)+0.3, sin(uv.x*2.0+0.999+uv.y*2.0*3.0)+0.1);
}

vec3 renderPsych(vec2 screenPos) {
    vec2 trPos = vec2(atan(screenPos.y, screenPos.x), length(screenPos));
    trPos.y += abs(sin(trPos.x*8.0+t*6.0))*0.2*((sin(t)+1.0)*0.4+0.4);
    return psychTexture(vec2(trPos.x+t, trPos.y+sin(t*1.2)*0.6+t));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 pos = (2.0*fragCoord.xy - iResolution.xy)/iResolution.y;
    fragColor = vec4(renderPsych(pos)*clamp(pow(length(pos),.5), 0.0, 1.0), 1.0);
}
