// "Periodic Pulse" function for sound synthesis - 2017 by JT

#define plot(Y) o += smoothstep(40./R.y, 0., abs(Y-U.y))
#define plot_r(Y) o.r += smoothstep(40./R.y, 0., abs(Y-U.y))
#define plot_g(Y) o.g += smoothstep(40./R.y, 0., abs(Y-U.y))

#define pi 3.1415926

float periodic_pulse(float s, float x)
{
    return exp(s * (-0.5 - 0.5 * cos(x))) - exp(s * (-0.5 + 0.5 * cos(x)));
}

void mainImage( out vec4 o, in vec2 U )
{
    float t = iTime;
    vec2 R = iResolution.xy;

    U = 10.* (2.*U-R)/R.y;
    o -= o;
    //float d = iMouse.y<=0. ? 0.5 + 0.5 * sin(t) : iMouse.y/R.y;
    //float d = 0.5 + 0.5 * sin(t);
    float d = 0.5 + 0.499 * sin(t);

    plot(periodic_pulse(1. / d, U.x) + 0.0);
    plot(0.0);

    //plot(  sin(t - U.x) + 0.);

    o.b += .2;
}

#define pi 3.1415926

float periodic_pulse(float s, float x)
{
    return exp(s * (-0.5 - 0.5 * cos(x))) - exp(s * (-0.5 + 0.5 * cos(x)));
}

vec2 mainSound( in int samp, float time )
{
    //float d = iMouse.y<=0. ? 0.5 + 0.5 * sin(t) : iMouse.y/R.y;
    //float d = 0.5 + 0.5 * sin(time);
    float d = 0.5 + 0.499 * sin(time);

    //return vec2(periodic_pulse(1.0 / d, 2.0 * pi * 440.0 * time));
    return (1.0 - d) * vec2(periodic_pulse(1.0 / d, 2.0 * pi * 440.0 * time));
}
