// Checkerboard by Nikos Papadopoulos, 4rknova / 2013
// WTFPL
vec3 checkerBoard( vec2 uv, vec3 col0, vec3 col1 )
{
    return mix( col0, col1, mod( uv.x + uv.y, 2.0 ) );   
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    // UV coord
    vec2 uvCheck = floor( 4.0 * fragCoord.xy * vec2( iResolution.x / iResolution.y, 1 ) / iResolution.xy );
    vec2 uv      = fragCoord.xy / iResolution.xy;

    // Light values
    float ambient = 0.25;
    float sun     = 0.50;

    // "Carefully" picked greys
    vec3 col0     = vec3( 0.0 + sun );
    vec3 col1     = vec3( 0.0 );

    // Fake shadow / gradient from white to black
    float shadow  = smoothstep( 0.15, 0.3, abs( uv.x - 0.5 ) );

    // Base color
    fragColor = vec4( checkerBoard( uvCheck, col0, col1 ) + ( sun * shadow ) + ambient, 1);

    // Cursor / reference
    if( fragCoord.x > iMouse.x - 10.0 && fragCoord.x < iMouse.x + 10.0 )
    {
        if( fragCoord.y > iMouse.y - 10.0 && fragCoord.y < iMouse.y + 10.0 )
        {
            fragColor = vec4( col0 + ambient, 1.0 );
        }
    }
}
