void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    float finf = 1.0 / clamp(uv.x, 0.0, 1.0);
    if(isinf(finf)) {
		fragColor = vec4(1, 0, 0, 1);
    } else {
        fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    }
}
