mat2 rot(float a)
{
    float c = cos(a), s = sin(a);
    mat2 m = mat2(vec2(c,s),vec2(-s,c));
    return m;
}

vec2 spin(vec2 v,float aaa)
{
    return rot(fract(aaa)*3.14159265) * v;
}

float g(vec2 v)
{
    return ( v.y - v.x );
}

float h(float x)
{
    return abs(fract(x)-0.5);
}

float j(float x)
{
    x = pow(x,1.5);
    x = abs(fract(x*5. + iTime)-0.5);
    return x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
