// hsv2rgb by Hugh Kennedy: https://github.com/hughsk/glsl-hsv2rgb (Links to an external site.)Links to an external site.
vec3 hsv2rgb(vec3 c) {
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

// rgb2hsv: http://lolengine.net/blog/author/sam (Links to an external site.)Links to an external site.
vec3 rgb2hsv(vec3 c)
{
 vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
 vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
 vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

 float d = q.x - min(q.w, q.y);
 float e = 1.0e-10;
 return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

// Luminance
float luma(vec3 color) {
  return dot(color, vec3(0.299, 0.587, 0.114));
}

float luma(vec4 color) {
  return dot(color.rgb, vec3(0.299, 0.587, 0.114));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 color = texture(iChannel0,uv);

    if(color.b > 0.6){
        color.b *= 1.8;
        color.r *= 0.2;
        color.g *= 0.2;
    }
    else{
        color.b *= 0.01;
        color.r *= 0.01;
        color.g *= 0.01;
    }

    fragColor = color;
}
