#define PI 3.14159265359
#define TAU 6.28318530718

#define SHARP_EDGE 1

float makeYinYang(vec2 uv){
	float vLength = length(uv);
	float curve = sin(uv.x * TAU) * 1./16.;
	float curveSign = sign(curve);
	curve = sqrt(abs(curve)) * curveSign;
	float clr = smoothstep(curve - .025, curve + .025, uv.y);
	float outerColor;
	{
		vec2 p = uv;
		float l = length(p);
		p.y *= -1.;//TODO this is bullshit
		p.y /= .2;
		float angle = 2.999;
		p *= mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
		float sign = sign(p.y);
		float offset = clamp(l - .5, 0., 1.5) * 2.;
		float curve = pow(abs(p.y * .125 + 0.125 * sign), 1./3.) * sign;
		outerColor = smoothstep(curve - offset/2., curve + offset/2., p.x);
	}

#ifndef SHARP_EDGE
    outerColor = 1. - outerColor;
#endif
	clr = mix(clr, outerColor, smoothstep(.5, .5 + .01, vLength));
	float dots = (1. - smoothstep(.05, .075, length(abs(uv) - vec2(.25, .0))));
	dots *= sign(uv.x);
	return clr + dots;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy * 2. - 1.;
	uv.x *= iResolution.x/iResolution.y;

	// float angle = -iTime * 2.;//cos(u_time * .5) * TAU;
	// uv *= mat2(cos(angle), -sin(angle), sin(angle), cos(angle));

	float yinYang = makeYinYang(uv);
	fragColor = vec4(vec3(yinYang), 1.);
}
