// Smooth triangle curve for sound synthesis - shadertoy-version 2017 by JT
// adapted from formula suggested by ybeltukov at https://mathematica.stackexchange.com/questions/38293/make-a-differentiable-smooth-sawtooth-waveform

#define plot(Y) o += smoothstep(40./R.y, 0., abs(Y-U.y))
#define plot_r(Y) o.r += smoothstep(40./R.y, 0., abs(Y-U.y))
#define plot_g(Y) o.g += smoothstep(40./R.y, 0., abs(Y-U.y))

#define pi 3.1415926

float smoothed_triangle(float d, float x)
{
    return 1.0 - 2.0 * acos(d * sin(x)) / pi;
}

void mainImage( out vec4 o, in vec2 U )
{
    float t = iTime;
    vec2 R = iResolution.xy;

    U = 10.* (2.*U-R)/R.y;
    o -= o;
    //float d = iMouse.y<=0. ? 0.5 + 0.5 * sin(t) : iMouse.y/R.y;
    float d = 0.5 + 0.5 * sin(t);

    plot(  smoothed_triangle(d, U.x) + 0.);

    //plot(  sin(t - U.x) + 0.);

    o.b += .2;
}

// Smooth triangle curve for sound synthesis - 2017 by JT
// (adapted from [url]https://mathematica.stackexchange.com/questions/38293/make-a-differentiable-smooth-sawtooth-waveform[/url] but using 1 - d instead of d).

#define pi 3.1415926

float smoothed_triangle(float d, float x)
{
    return 1.0 - 2.0 * acos(d * sin(x)) / pi;
}

vec2 mainSound( in int samp, float time )
{
    //float d = iMouse.y<=0. ? 0.5 + 0.5 * sin(t) : iMouse.y/R.y;
    float d = 0.5 + 0.5 * sin(time);

    return vec2(smoothed_triangle(d, 2.0 * pi * 440.0 * time));
}
