float addGull(vec2 uv, vec2 pos, float size, float wing) {
	vec2 q = (uv - pos) / size;
    float h = 0.004;
    float w = 0.009;
    float v = 1.0 - (1.0 - smoothstep(h, h, abs(q.y - 0.015))) * (1.0 - smoothstep(w, w, abs(q.x)));

    float r = 0.0 + 0.07 * cos(atan(q.y, q.x) * 10.0 + wing * q.x);
    v *= 1.0 - (1.0 - smoothstep(r, r, length(q))) * smoothstep(0.015, 0.015, q.y);
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 q = uv - vec2(0.3, 0.7);
    vec3 color = mix(vec3(1.0, 0.4, 0.1), vec3(1.0, 0.8, 0.3), 1.1 * sqrt(uv.y));

    // leaves
    float r = 0.2 + 0.1 * cos(atan(q.y, q.x) * 11.0 + 20.0 * q.x - 1.0);
    color *= smoothstep(r, r + 0.01, length(q));

    // trunk
    float w = 0.02;
    w += cos(150.0 * q.y) * 0.001;

    // ground
    w += exp(-50.0 * uv.y);

    float h = 0.1;
    color *= 1.0 - (1.0 - smoothstep(w, w, abs(q.x - 0.2 * sin(2.0 * q.y)))) * (1.0 - smoothstep(h, h, q.y));

    // gulls
    color *= addGull(uv, vec2(0.7, 0.3), 1.0, 100.0);
    color *= addGull(uv, vec2(0.90, 0.45), 0.7, 130.0);
    color *= addGull(uv, vec2(0.78, 0.38), 0.7, 95.0);
    color *= addGull(uv, vec2(0.85, 0.2), 0.6, 120.0);

	fragColor = vec4(color, 1.0);
}
