float iter(vec2 c)
{
   vec2 zn = vec2(0.0);
   for(int i=0; i<400; i++)
   {
       vec2 nzn;
       nzn.x = zn.x*zn.x - zn.y*zn.y + c.x;
       nzn.y = 2.0*zn.x*zn.y + c.y;
       zn = nzn;
       if(length(zn)>2.0)
       {
           float a = zn.x;
           float b = zn.y - zn.x;
           return length(vec2(cos(a),sin(b)));
       }
   }
    return 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv-=vec2(0.5,0.5);
    float zz = (mod(iTime,26.0)+2.0)*0.25;
    uv+=vec2(cos(iTime), sin(iTime))*0.1;

    uv/=pow(zz,zz)*0.4;
    uv-=0.56;
	float val = iter(uv);

    vec3 samp = texture(iChannel0,vec2(val + vec2(iTime))).xyz;
    float mask = 0.0;
    if(val>0.0) mask = 1.0;

    fragColor = vec4(samp,1.0)*mask;
}
