precision highp float;

vec2 f(vec2 x, vec2 c) {
	return mat2(x,-x.y,x.x)*x + c;
}

vec3 palette(float t, vec3 a, vec3 b, vec3 c, vec3 d) {
    return a + b*cos( 6.28318*(c*t+d) ); /* thanks, iq */
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
    vec2 c = vec2(-iResolution.x/iResolution.y + cos(iTime/4.0)/4.0, sin(iTime/4.0)/4.0) + abs(sin(iTime/8.0))*(uv * 4.0 - vec2(2.0));
    vec2 x = vec2(0.0);
    bool escaped = false;
    int iterations = 0;   
    for (int i = 0; i < 1000; i++) {
        if (float(i) > 10.0 + abs(sin(iTime / 2.0)) * 40.0) break;
        iterations = i;
    	x = f(x, c);
        if (length(x) > 2.0) {
            escaped = true;
            break;
        }
    }

	fragColor =
        escaped 
        	? vec4(palette(float(iterations) / 50.0,  vec3(0.3, 0.2, 0.4), vec3(0.2, 0.1, 0.0), vec3(1.0), vec3(0.3,0.5,0.2)),1.0)
        	: vec4(0.0, 0.0, 0.0, 1.0);
}
