#define CS(n) (0.5*cos(iTime*n) + 0.5)
#define COS(n) cos(iTime*n)
#define PI 3.1415926535897932384626

vec3 cc(float x, vec3 a, vec3 b, vec3 c, vec3 d)
{
    return a + b*cos(2.0*PI*(c*x + d));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy *2.0 - 1.0;
    p.x *= iResolution.x/iResolution.y;

    float l = length(p);
    p.x += 0.05*sin(0.27*iTime + l*4.1);

    float w = smoothstep(CS(1.)*COS(0.5), CS(1.)*COS(0.5)+0.01, p.x);

    vec3 color = mix(vec3(0.8, 0.6, 0.1), vec3(1.0, 1.0, 0.0), p.x);
    if (w < 0.1)
        color = cc(p.x, vec3(0.5), vec3(0.5), vec3(1.0), vec3(0.1, 0.2, 0.3));

	fragColor = vec4(color,1.0);
}
