//
// kaiware raymarching
//
// Original Raymarching code(CalcNormal, CalcAO)
// Deformed by XBE
// https://www.shadertoy.com/view/XdfSDB

// Distance functions by iq
// https://iquilezles.org/articles/distfunctions
float sdRoundBox(vec3 p, vec3 size, float r)
{
    return length(max(abs(p) - size * 0.5, 0.0)) - r;
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r)
{
	vec3 pa = p - a, ba = b - a;
	float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
	return length(pa - ba*h) - r;
}

float sdEllipsoid( vec3 p, vec3 r )
{
    float k0 = length(p/r);
    float k1 = length(p/(r*r));
    return k0*(k0-1.0)/k1;
}

float sdKaiware(vec3 p, float sc)
{

	// scale
	p = p / sc;

	// head - 頭部
	float d1 = sdRoundBox(p, vec3(1, 0.8, 1), 0.1);

	// beak - くちばし
	float d2_0 = sdRoundBox(p - vec3(0, -0.2, 0.7), vec3(0.8, 0.25, 0.4), 0.1);
	float d2_1 = sdRoundBox(p - vec3(0, -0.0, 0.7), vec3(1.1, 0.35, 1.1), 0.0);
	float d2_2 = sdRoundBox(p - vec3(0, -0.4, 0.7), vec3(1.1, 0.35, 1.1), 0.0);
	float d2_3 = sdRoundBox(p - vec3(0, -0.2, 0.7), vec3(0.75, 0.1, 0.35), 0.1);

	float d2_top = max(d2_0, d2_1);
	float d2_bottom = max(d2_0, d2_2);
	float d2 = min(min(d2_top, d2_bottom), d2_3);

	// stem - はっぱの茎
	float d3_0 = sdCapsule(p, vec3(0, 0.5, 0), vec3(0, 0.75, 0), 0.05);
	// leaf - 葉っぱ
	float d3_1 = sdEllipsoid(p - vec3(0.2, 0.75, 0), vec3(0.2, 0.025, 0.1));
	float d3_2 = sdEllipsoid(p - vec3(-0.2, 0.75, 0), vec3(0.2, 0.025, 0.1));
	float d3 = min(d3_0, min(d3_1, d3_2));

	// eyes - 目
	float d4_0 = sdCapsule(p, vec3(0.2, 0.25, 0.6), vec3(0.4, 0.2, 0.6), 0.025);
	float d4_1 = sdCapsule(p, vec3(-0.2, 0.25, 0.6), vec3(-0.4, 0.2, 0.6), 0.025);
	float d4 = min(d4_0, d4_1);

	// combine - 合成
	float sum = max(min(min(d1, d2), d3), -d4);

	sum *= sc;

	return sum;
}

vec3 rotate(vec3 p, float angle, vec3 axis)
{
    vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float r = 1.0 - c;
    mat3 m = mat3(
        a.x * a.x * r + c,
        a.y * a.x * r + a.z * s,
        a.z * a.x * r - a.y * s,
        a.x * a.y * r - a.z * s,
        a.y * a.y * r + c,
        a.z * a.y * r + a.x * s,
        a.x * a.z * r + a.y * s,
        a.y * a.z * r - a.x * s,
        a.z * a.z * r + c
    );
    return m * p;
}

float map( in vec3 pos )
{    

    //pos = repeat(pos, vec3(1.5, 1.5, 1.5));

    pos = rotate(pos, iTime * -2.5, vec3(0,1,0));

    float l = sdKaiware(pos, 0.5);

    return l;
}

vec3 calcNormal( in vec3 pos )
{
	vec3 eps = vec3(0.001,0.0,0.0);
	return normalize( vec3(
		map(pos+eps.xyy) - map(pos-eps.xyy),
		map(pos+eps.yxy) - map(pos-eps.yxy),
		map(pos+eps.yyx) - map(pos-eps.yyx) ) );
}

// Raymarching
bool raymarch(vec3 origin, vec3 dir, out float dist, out vec3 norm)
{
	float epsilon = 0.001;
	float maxdist = 10.0;
	float marched = 0.0;
	float delta = 2.*epsilon;
	dist = -1.0;
	for (int steps=0; steps < 128; steps++)
	{
		if ( ( abs(delta) < epsilon ) || (marched > maxdist) ) continue;
		delta *= 0.5;

        marched += delta;
        delta = map(origin + marched * dir);
	}
	bool res = false;
	if (marched < maxdist)
	{
		norm = calcNormal(origin + marched * dir);
		dist = marched;
		res = true;
	}
	return res;
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float totao = 0.0;
    float sca = 1.0;
    for( int aoi=0; aoi<5; aoi++ )
    {
        float hr = 0.01 + 0.05*float(aoi);
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos );
        totao += -(dd-hr)*sca;
        sca *= 0.75;
    }
    return clamp( 1.0 - 4.0*totao, 0.0, 1.0 );
}

vec3 render( in vec3 ro, in vec3 rd )
{ 
    vec3 col = vec3(0.0);
	float dist = 0.;
	vec3 nor = vec3(0.,0.,0.);
	//
	vec3 lig = normalize( vec3(-0.6, 0.7, -0.5) );
	vec3 sky = vec3(0.32,0.36,0.4) - rd.y*0.4;
	float sun = clamp( dot(rd,lig), 0.0, 1.0 );
	sky += vec3(1.0,0.8,0.4)*0.5*pow( sun, 10.0 );
	sky *= 0.9;
	//
    if ( raymarch(ro, rd, dist, nor) )
    {
        vec3 pos = ro + dist*rd;

		col = vec3(0.65, 1.0, 0.3);

        float ao = calcAO( pos, nor );

		float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
        float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);

		vec3 brdf = vec3(0.0);
		brdf += 1.20*amb*vec3(0.20,0.22,0.26)*ao;
        brdf += 0.20*bac*vec3(0.15,0.15,0.15)*ao;
        brdf += 1.00*dif*vec3(1.00,0.90,0.70);

		float pp = clamp( dot( reflect(rd,nor), lig ), 0.0, 1.0 );
		float spe = pow(pp,16.0);
		float fre = ao*pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );

		col = col*brdf + vec3(0.8)*vec3(1.00,0.70,0.60)*spe + 0.2*fre*(0.5+0.5*col);
		col = mix( col, sky, 1.0-exp(-0.0025*dist*dist*dist) );
	}
	else
	{
		col = sky;
	}

	return vec3( clamp(col,0.0,1.0) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy/iResolution.xy;
    vec2 p = -1.0+2.0*q;
	p.x *= iResolution.x/iResolution.y;

    // camera
    vec3 ro = vec3( 0., 0.75, -2. );
    // target
	vec3 ta = vec3(0., 0., 0.);

	// camera tx
	vec3 cw = normalize( ta-ro );
	vec3 cp = vec3( 0.0, 1.0, 0.0 );
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
	vec3 rd = normalize( p.x*cu + p.y*cv + 2.5*cw );

    vec3 col = render( ro, rd );
	col = sqrt(col);

	fragColor=vec4( clamp(col,0.,1.), 1.0 );
}
