void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    vec4 noise = texture(iChannel1, uv);
    vec3 c1 = vec3(0.8, 0.7, 0.0);
    vec3 c2 = vec3(0.6, 0.1, 0.0);
    float intensity = abs(noise[0] - 0.25) + abs(noise[1] - 0.125) + abs(noise[2] - 0.065);
    intensity = clamp(intensity * 0.5, 0.0, 1.0);
    //float intensity = noise[0];
    vec3 c = mix(c1, c2, intensity);
    fragColor = vec4(c,1.0);
}
