void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 p1 = vec2(iMouse.xy/iResolution.xy);
    vec4 p1Color = vec4(1., 0., 0., 1.);

    vec2 p2 = vec2(0.8, 0.5);
    vec4 p2Color = vec4(0., 0., 1., 1.);

    vec2 p3 = vec2(0.5, 0.7);
    vec4 p3Color = vec4(0., 1., 0., 1.);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uvOriginal = fragCoord/iResolution.xy;
    vec2 uv = fragCoord/iResolution.xy;

	float noiseLevel = 2. + abs(sin(iTime/5.)) * 2.;
    uv.x -= noiseLevel / 20.;
    uv.y -= noiseLevel / 20.;
    uv.x += (snoise(vec3(uv.x*noiseLevel,uv.y*noiseLevel,iTime/10.))+1.)/2./2.;
    uv.y += (snoise(vec3(uv.x*noiseLevel,uv.y*noiseLevel,iTime/10.+1234.))+1.)/2./2.;
    if(uv.x>1.){
        uv.x=1.;
    } else if(uv.x<0.){
        uv.x=0.0;
    }
    if(uv.y>1.){
        uv.y=1.;
    } else if(uv.y<0.){
        uv.y=0.0;
    }

    vec3 mixColor = (
        				(1.-distance(p1,uv))*p1Color.rgb*3. + 
                     	(1.-distance(p2,uv))*p2Color.rgb*3. + 
                     	(1.-distance(p3,uv))*p3Color.rgb*3.
                    ) 
        			/3.;
    //mixColor += vec3(noise(vec3(uvOriginal*1000000.,iTime*20.))-.5)/10.;

    if(uvOriginal.x > .5){
    	mixColor = 1. - mixColor;
    }
    if(uvOriginal.y > .5){
    	mixColor = 1. - mixColor;
    }
    if (uvOriginal.x > .5 && uvOriginal.y > .5) {
    	//mixColor = vec3(mixColor.r*.8, mixColor.g*1.2, mixColor.g);
    }

    fragColor = vec4(mixColor,1.0);

}
