vec2 distort( vec2 p )
{
  float theta  = atan(p.y, p.x);
  float radius = length(p);
  radius = pow(radius, 2.41);
  theta += sin(iTime) * 3.14;
  p.x = radius * cos(theta);
  p.y = radius * sin(theta);
  return 0.5 * (p + 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (fragCoord.xy / iResolution.xy) - vec2(0.5, 0.5);
  fragColor = texture(iChannel0, distort(uv * 2.0), 1.0 + sin(iTime) * 5.0 );
  fragColor.r *= abs(sin(iTime * 10.0));
  fragColor.g *= abs(sin(iTime * 10.0 + 1.0));
  fragColor.b *= abs(sin(iTime * 10.0 + 2.0));
}
