// check your gamma setting before (should be 2.2) :
//     https://www.shadertoy.com/view/llfSRM  or  https://www.shadertoy.com/view/Xdl3DM

void mainImage( out vec4 O, vec2 U )
{
    float x = U.x/iResolution.x;

	O =  U.y<10. && mod(x,.3)<2./iResolution.x ? O-O       // ticks

        : x<.3 || x > .9
        ? vec4(pow(.5,1./2.2))                             // gamma transformed mid-gray 
        : x<.6 
            ? vec4(fract((U.x+U.y)/2.)<.5)                 // checker pattern
            : vec4(fract((float(iFrame)+U.x+U.y)/2.)<.5);  // time swapping checker pattern


}
