float distrt(float x)
{
    for(int i=0;i<6;i++){
        x = x*x;
        x = 1.0-x;
    }
    return x;
}

void mainImage1( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float u = uv.x+iTime*0.05 + sin(uv.y*3.1415926536*2.0 * 8.0) * 0.02;
    float red = distrt((sin(u * 3.1415*2.0*5.0)*0.5+0.5));
    float green = uv.y;
    float blue = 0.5+0.5*sin(iTime);
    float alpha = 1.0;
	fragColor = vec4(red, green, blue, alpha);
}

