#define SAMPLES 128
#define RADIUS .03
#define INTENSITY .7

vec3 getVolumetric(vec2 uv, vec2 c) {
    vec3 sum = vec3(0.);
    float  w = 1./float(SAMPLES);
    for(int i = 0; i < SAMPLES; i++) {
        sum += 1.-texture(iChannel0, uv).w;
        uv += (c-uv)*RADIUS;
    }
    return sum * w * INTENSITY;
}

vec3 blend(vec3 a, vec3 b) {
    return 1.-(1.-a)*(1.-b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);

    vec2 c = iMouse.z > 0. ? iMouse.xy/iResolution.xy : vec2(.3,.5);

    fragColor.xyz *= fragColor.w;
    fragColor.xyz  = blend(fragColor.xyz, getVolumetric(uv,c));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    fragColor = texture(iChannel0, uv);

    float l = length(fragColor.xyz-vec3(0.,1.,0.));
    fragColor.w = smoothstep(.55,.75,l);
}
