vec3 MandelBrot(vec2 p){
        float time = iTime;

        float coa = cos( time );
        float sia = sin( time );
        vec2 xy = mat2(coa,sia,-sia,coa)*p;
        vec2 c =  xy;

        vec2 z=vec2(0);
        float l=0.0;

    	int sinTimeFrac = int((sin(time) + 1.0)*2.0);

        for(int i=0;i<256;i++){

            for(int j=0;j<sinTimeFrac;j++){
                z = mat2(z, -z.y, z.x)*z;
            }

            z = mat2(z,-z.y,z.x)*z + c;

            if( dot(z,z)>4.0 ) break;
            l++;
        }

    	if( dot(z,z)<4.0 )l=sqrt(dot(xy,z))*4.0;
    	float sl = (log(dot(z,z)/l));

        float al = smoothstep( 0.1, 0.4, (sin(iTime)+1.0)/2.0);
        l = mix( l, sl, al );

        vec3 col = vec3(l*0.2,0.0,0.0);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    fragColor = vec4(MandelBrot(p),0);
}
