void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	vec2 xy = 0.5 * uv - 0.9;
    // Time varying pixel color
    vec4 col = texture(iChannel0, uv);
    //Automatic
    // col.r -= distance(uv*2.0, vec2(cos(iTime)+1.2,sin(iTime)+1.2)) * 1.0;
    // col.g -= distance(uv*2.0, vec2(cos(iTime)+1.2,sin(iTime)+1.2)) * 1.0;
    // col.b -= distance(uv*2.0, vec2(cos(iTime)+1.2,sin(iTime)+1.2)) * 1.0;

    // Manual
    col.r -= distance(uv, iMouse.xy/iResolution.xy) * 2.5;
   	col.g -= distance(uv, iMouse.xy/iResolution.xy) * 2.5;
    col.b -= distance(uv, iMouse.xy/iResolution.xy) * 2.5;
    col.a = 1.0;

    // Output to screen
    fragColor = col;
}
