void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
         M = iMouse.xy;
    float t = iTime;
    if ( length(M) > 10. ) M /= R;
    else M = .5 + .5* vec2( cos(t)+.5*sin(-3.7*t), cos(.317*t)+.5*cos(4.21*t) ) / 1.5;

  //vec4 C = vec4(2.*M,.5,0);
    vec4 C =   2.* M.y 
            * ( .5 + .5 * cos( 6.3 *  M.x  + vec4(0,23,21,0) )); // https://www.shadertoy.com/view/ll2cDc
    O = C / length(3.*(U+U-R)/R.y);

    U *= 4./R; 

#define S(p,c) if ( abs(U.x-p) < .05 ) O-=O, O.c = step( U.y, C.c )
    S( .3, r );
    S( .5, g );
    S( .7, b );
    if ( U.x < .8 && abs (U.y-1.) < .01 ) O++;
}
