void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  // Normalized pixel coordinates (from 0 to 1)
  vec2 uv = fragCoord/iResolution.xy;

  // Time varying pixel color
  vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

  float taille_damier = 20.f + (sin(iTime)+1.f/2.f) * 10.f;

  int v_pair = floor(mod(fragCoord.y/taille_damier,2.f)) == 0.f ? 1 : 0;
  int h_pair = floor(mod(fragCoord.x/taille_damier,2.f)) == 0.f ? 1 : 0;

  bool pair = ((v_pair + h_pair) % 2) == 0;

  // Output to screen
  //fragColor = pair ? vec4(1.f,1.f,1.f,1.0) : vec4(0.f,0.f,0.f,1.0);
  fragColor = pair ? vec4(1.f,1.f,1.f,1.0) : vec4(col,1.0);
}
