void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;

    float uvXA = uv.x-0.5;
    if (uvXA < 0.0) {
        uvXA = -uvXA;
    }

    float sUV = uv.y+sin(uvXA*20.0)+  ( (sin((uv.x+(iTime/10.0))*314.0) + 1.0) / 60.0   )   +  iTime;
    if (mod(sUV,0.4) > 0.2) {
		fragColor = vec4(0.14,mod(sUV,1.0),0.1,1.0);
    } else {
        if (mod(sUV-0.1,0.4) > 0.2) {
            fragColor = vec4(0.1,mod(sUV-0.1,1.0),0.2,1.0);
        } else {
            fragColor = vec4(0.1,0.1,0.1,1.0);
        }
    } 
}
