#define time  iTime
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float k=2.;
vec3 color = vec3(0.11, 1., 0.);
vec2 pos = (fragCoord.xy/iResolution.xy) - .5; 
vec3 d = vec3(pos, 0.0);

 //----------------------------------
    float t = 2.34;//time;
   vec2 sc = vec2(sin(t), cos(t));
   mat3 rotate;
   rotate = mat3( sc.y,  0.0, -sc.x,
                     0.0,   1.0,  0.0,
                     sc.x,  0.0, sc.y);   

   d = d *  rotate;
  vec3 o = d, c=color *k*d, p;  
 //----------------------------------
    for( int i=0; i< 9; i++ )
    {
        p = o + cos(time * 0.1);
      for (int j = 0; j < 9; j++) 
      {
          p.y += 0.02/ float(j + 1) * sin(float(j + 1) * p.x + (time * 20.0)/20. + 0.3 * float(j + 10)) - 18. + 15. ;

          p = abs(p);
          k += exp(-8. * abs(dot(p,o)));
      }

      k/=3.;
        o += d * 0.5 /k;
        c = 1.1 * c + .1 * k *vec3(k*k,k,sin(time));
    }
    c =  .4 *log(1.+c);
    fragColor.rgb = c;
}
