void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float tm=iTime*0.3;
	//vec2 uv = fragCoord.xy / iResolution.xy;
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    float p=1./((1.-fragCoord.y/iResolution.y)+.125);
    float x=p*(sin(tm*0.1)*0.5 + fragCoord.x / iResolution.y)*2.-1.5,
        y=p*(sin(tm*0.13)*.5 + fragCoord.y / iResolution.y)*2.-1.,
        t;
    t=x;

    x=x*cos(tm) + y*sin(tm);
    y=y*cos(tm) - t*sin(tm);
    float a=x,b=y,m=0.;
    int j=0;
    for(int i=0; i<11;i++){
        m=x*x+y*y;
        if(m>16.){j++;continue;}
        t=x;
        x=x*x-y*y+a;
        y=2.*t*y+b;
    }
    //fragColor=vec4(sin(x)*0.5+0.5,sin(y)*0.5+0.5,0.,1.);
    m=log(log(m))*1.49+float(j);

    fragColor=vec4(
        vec3(sin(x)*0.5+0.5,cos(y)*0.5+0.5,0.5-sin(m)*0.5)*(sin(m*0.2)*0.25+0.75)
       	,1.);
}
