void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;//Condensing this into one line
    vec4 texColor = texture(iChannel1, uv);//Get the pixel at xy from iChannel0

    float colorDist = distance(vec4(0.8,0.05,0.05,1.0), texColor);

   	//if(texColor.g > 0.3 && texColor.r < 0.25 && texColor.b < 0.25) {
    if(colorDist > 0.9) {
       texColor = texture(iChannel0, uv);
    }

    fragColor = texColor;
}
