// Entry for [2TC 15] (= 280 chars or less, as counted by Shadertoy)
void mainImage (out vec4 f, vec2 p) {
	f *= 0.;
	vec4 r = f, P = f, T;
	r.xy = p / iResolution.y - .8;
	++r.y;
	for (int i = 0; i < 42; ++i)
		if ((p.x = cos (P.x * P.y + iTime * 4.) - 15.) < p.y)
			P += T = p.x > (p.y = min (f.x, f.y) * (r.y - 2.)) ? f - f : step (f, f.yxzw) * sign (r),
			f += T / r;
	f = .4 + .4 * sin (P) - .2 * T.x - .1 * T.y;
}

