uniform struct points { vec2 p[4]; };

struct ind_dist { vec2 v; float dist; };

ind_dist get_nearest(points p, vec2 uv)
{

    float minval = 1e10;
    vec2  v;
    for (int i = 0; i < 4; i ++) {
        float d = distance(uv, p.p[i]);
        minval = min(d,minval);
        if (minval == d) {
            v = p.p[i];
        }
    }

    ind_dist o;
    o.v = v;
    o.dist  = minval;
	return o;
}

float ani(float iTime){
    return abs(sin(1.0*iTime));
}

vec2 getuv(float scale, vec2 uv) {
    // Get Normalized UV Coordinates
	uv = (uv - 0.5);
   	uv[0] = 1.8*uv[0];
    uv = scale*uv; 
    return uv;
}

vec2 sphereinv(vec2 x, float r){
    return r*x/(x[0]*x[0] + x[1]*x[1]);
}

mat2 rotate_mat(float theta) {
    // 2x2 Rotation Matrix
    return mat2( cos( theta ), -sin( theta ),
                 sin( theta ),  cos( theta ));    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float t = iTime;

    points p;
    p.p[0] = vec2( 0.0,  2.0 );
    p.p[1] = vec2( 2.0 , 0.0);
    p.p[2] = vec2( -2.0, 0.0);
    p.p[3] = vec2( 0.0, -2.0);

    vec2 uv = getuv(0.2 + mod(-0.2*t,0.2),fragCoord.xy / iResolution.xy);

	ind_dist I;
    const int iters = 20;

    float depth = 0.0;
    for (int i = 0; i < iters; i++) {
        I = get_nearest(p, uv);
        vec2 closest_pt = I.v;
        uv = (2.0)*(uv - closest_pt) + closest_pt;
        if (length(uv) < 1.2 + 0.5*ani(1.0*t)){
            uv = sphereinv(uv,1.0);
    	}
        depth = depth + I.dist;
    }

    float minval = I.dist/pow(2.0,float(iters));

    depth = depth/pow(2.0,float(iters-10));

    float r; float g; float b;

    r = smoothstep(0.0,5.0 - mod(t,2.0),depth);
    g = smoothstep(0.0,5.0 - mod(t,2.0),depth);
    b = smoothstep(0.0,5.0 - mod(t,2.0),depth);


    fragColor = vec4(r,g,b,1.0);

}
